mapper.insert:
		 insert into community (artseq,uid, boardtype, title, content) values (
            #{artseq},#{uid},#{boardType},#{title},#{content}
        )

mapper.update:
  update community set title = #{title}, content=#{content} where uid like #{uid} 
	and artseq like #{artseq}
	and  boardtype like '게시판'
